import React from "react";
import { useMutation } from "@tanstack/react-query";
import {
  XMarkIcon,
  PaperAirplaneIcon,
  DocumentTextIcon,
  ClockIcon,
} from "@heroicons/react/24/outline";
import { emailApi } from "../services/api";

interface AiReplyGeneratorProps {
  emailId: string;
  originalEmail: {
    from: string;
    subject: string;
    body?: string;
  };
  onClose: () => void;
  onReplySent?: () => void;
}

const AiReplyGenerator: React.FC<AiReplyGeneratorProps> = ({
  emailId,
  originalEmail,
  onClose,
  onReplySent,
}) => {
  // 🔹 AI reply generation
  const generateReplyMutation = useMutation({
    mutationFn: () =>
      emailApi.generateAiReply(emailId, {
        from: originalEmail.from,
        subject: originalEmail.subject,
        body: originalEmail.body || "",
      }),
  });

  // 🔹 Sending reply
  const sendReplyMutation = useMutation({
    mutationFn: (data: { emailId: string; reply: string; subject: string }) =>
      emailApi.sendReply(data.emailId, data.reply, "current-user", data.subject),
    onSuccess: () => {
      onReplySent?.(); // ✅ update parent
      onClose(); // ✅ close modal
    },
    onError: (error) => {
      console.error("Error sending reply:", error);
      alert("Failed to send reply. Please try again.");
    },
  });

  const handleGenerateReply = () => {
    generateReplyMutation.mutate();
  };

  const handleSendReply = () => {
    if (generateReplyMutation.data?.generatedReply) {
      sendReplyMutation.mutate({
        emailId,
        reply: generateReplyMutation.data.generatedReply,
        subject: `Re: ${originalEmail.subject}`,
      });
    } else {
      alert("No AI reply generated yet.");
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white rounded-lg shadow-lg w-full max-w-2xl p-6 relative">
        {/* Close button */}
        <button
          onClick={onClose}
          className="absolute top-3 right-3 text-gray-400 hover:text-gray-600"
        >
          <XMarkIcon className="h-6 w-6" />
        </button>

        <h2 className="text-lg font-semibold text-gray-800 mb-4 flex items-center">
          <DocumentTextIcon className="h-5 w-5 mr-2" />
          AI Reply Generator
        </h2>

        <div className="mb-4">
          <p className="text-sm text-gray-600">
            <strong>From:</strong> {originalEmail.from}
          </p>
          <p className="text-sm text-gray-600">
            <strong>Subject:</strong> {originalEmail.subject}
          </p>
          <p className="text-sm text-gray-600 whitespace-pre-wrap mt-2">
            {originalEmail.body || "(No body)"}
          </p>
        </div>

        {/* Generated Reply Box */}
        {generateReplyMutation.isPending ? (
          <div className="p-4 text-gray-600 flex items-center">
            <ClockIcon className="h-5 w-5 mr-2 animate-spin" />
            Generating AI reply...
          </div>
        ) : generateReplyMutation.data ? (
          <textarea
            value={generateReplyMutation.data.generatedReply}
            readOnly
            className="w-full p-3 border rounded-md text-gray-700 bg-gray-50 mb-4"
            rows={6}
          />
        ) : (
          <p className="text-sm text-gray-500 italic mb-4">
            Click "Generate AI Reply" to create a smart response.
          </p>
        )}

        {/* Buttons */}
        <div className="flex justify-end space-x-3">
          {!generateReplyMutation.data ? (
            <button
              type="button"
              onClick={handleGenerateReply}
              disabled={generateReplyMutation.isPending}
              className="inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-blue-600 text-base font-medium text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 sm:text-sm disabled:opacity-50"
            >
              Generate AI Reply
            </button>
          ) : (
            <button
              type="button"
              onClick={handleSendReply}
              disabled={sendReplyMutation.isPending}
              className="inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-green-600 text-base font-medium text-white hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 sm:text-sm disabled:opacity-50"
            >
              {sendReplyMutation.isPending ? (
                <>
                  <ClockIcon className="h-4 w-4 mr-2 animate-spin" />
                  Sending...
                </>
              ) : (
                <>
                  <PaperAirplaneIcon className="h-4 w-4 mr-2" />
                  Send Reply
                </>
              )}
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default AiReplyGenerator;
