import React from 'react';
import { useMutation } from '@tanstack/react-query';
import {
  XMarkIcon,
  PaperAirplaneIcon,
  DocumentTextIcon,
  ClockIcon,
  SparklesIcon
} from '@heroicons/react/24/outline';
import { emailApi } from '../services/api';

interface OriginalEmail {
  from: string;
  subject: string;
  body?: string;
}

interface AiReplyGeneratorProps {
  emailId: string;
  originalEmail?: OriginalEmail; // âœ… now optional
  onClose: () => void;
  onReplySent?: () => void;
}

export default function AiReplyGenerator({
  emailId,
  originalEmail,
  onClose,
  onReplySent
}: AiReplyGeneratorProps) {
  // Generate AI reply mutation
  const generateReplyMutation = useMutation({
    mutationFn: () => emailApi.generateAiReply(emailId),
    onError: (error) => {
      console.error('Error generating AI reply:', error);
      alert('Failed to generate AI reply. Please try again.');
    }
  });

  // Send reply mutation
  const sendReplyMutation = useMutation({
    mutationFn: (data: { emailId: string; reply: string; subject?: string }) =>
      emailApi.sendReply(data.emailId, data.reply, 'current-user', data.subject),
    onSuccess: () => {
      onReplySent?.(); // update parent
      onClose(); // close modal
    },
    onError: (error) => {
      console.error('Error sending reply:', error);
      alert('Failed to send reply. Please try again.');
    }
  });

  const handleGenerateReply = () => {
    generateReplyMutation.mutate();
  };

  const handleSendReply = () => {
    if (generateReplyMutation.data?.generatedReply) {
      sendReplyMutation.mutate({
        emailId,
        reply: generateReplyMutation.data.generatedReply,
        subject: originalEmail ? `Re: ${originalEmail.subject}` : undefined
      });
    } else {
      alert('No AI reply generated yet.');
    }
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-2xl relative">
        {/* Close button */}
        <button
          onClick={onClose}
          className="absolute top-3 right-3 text-gray-400 hover:text-gray-600"
        >
          <XMarkIcon className="h-6 w-6" />
        </button>

        {/* Header */}
        <h2 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
          <SparklesIcon className="h-5 w-5 text-indigo-500 mr-2" />
          AI Reply Generator
        </h2>

        {/* Original Email (optional) */}
        {originalEmail && (
          <div className="mb-4 border-l-4 border-gray-200 pl-4 text-sm text-gray-600">
            <p>
              <strong>From:</strong> {originalEmail.from}
            </p>
            <p>
              <strong>Subject:</strong> {originalEmail.subject}
            </p>
            {originalEmail.body && (
              <p className="mt-2 line-clamp-3">{originalEmail.body}</p>
            )}
          </div>
        )}

        {/* AI Reply Box */}
        <div className="mb-4">
          {generateReplyMutation.isPending ? (
            <div className="flex items-center text-gray-500">
              <ClockIcon className="h-5 w-5 mr-2 animate-spin" />
              Generating AI reply...
            </div>
          ) : generateReplyMutation.data ? (
            <div className="border rounded-md p-3 bg-gray-50 text-sm text-gray-800">
              {generateReplyMutation.data.generatedReply}
            </div>
          ) : (
            <p className="text-sm text-gray-400 italic">
              No AI reply generated yet.
            </p>
          )}
        </div>

        {/* Actions */}
        <div className="flex justify-end space-x-3">
          {!generateReplyMutation.data ? (
            <button
              type="button"
              onClick={handleGenerateReply}
              disabled={generateReplyMutation.isPending}
              className="inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-indigo-600 text-base font-medium text-white hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 sm:text-sm disabled:opacity-50"
            >
              {generateReplyMutation.isPending ? (
                <>
                  <ClockIcon className="h-4 w-4 mr-2 animate-spin" />
                  Generating...
                </>
              ) : (
                <>
                  <DocumentTextIcon className="h-4 w-4 mr-2" />
                  Generate AI Reply
                </>
              )}
            </button>
          ) : (
            <button
              type="button"
              onClick={handleSendReply}
              disabled={sendReplyMutation.isPending}
              className="inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-green-600 text-base font-medium text-white hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 sm:text-sm disabled:opacity-50"
            >
              {sendReplyMutation.isPending ? (
                <>
                  <ClockIcon className="h-4 w-4 mr-2 animate-spin" />
                  Sending...
                </>
              ) : (
                <>
                  <PaperAirplaneIcon className="h-4 w-4 mr-2" />
                  Send Reply
                </>
              )}
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
