import React, { useState } from 'react';
import { useMutation } from '@tanstack/react-query';
import { XMarkIcon, SparklesIcon } from '@heroicons/react/24/outline';
import { emailApi } from '../services/api';
import { AiReplyRequest, FeedbackItem } from '../types';

interface AiReplyGeneratorProps {
  emailId: string;
  onClose: () => void;
}

const AiReplyGenerator: React.FC<AiReplyGeneratorProps> = ({ emailId, onClose }) => {
  const [request, setRequest] = useState<Partial<AiReplyRequest>>({
    emailId,
    tone: 'professional',
    style: 'detailed',
    includeToneFeedback: true,
    includeClarityFeedback: true,
  });

  const generateReplyMutation = useMutation({
    mutationFn: (request: AiReplyRequest) => emailApi.generateAiReply(request),
    onSuccess: (data) => {
      // Handle success - you might want to update the email or show the generated reply
      console.log('Generated reply:', data);
    },
  });

  const handleGenerate = () => {
    if (request.emailId) {
      generateReplyMutation.mutate(request as AiReplyRequest);
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'high':
        return 'text-red-600 bg-red-50 border-red-200';
      case 'medium':
        return 'text-yellow-600 bg-yellow-50 border-yellow-200';
      case 'low':
        return 'text-green-600 bg-green-50 border-green-200';
      default:
        return 'text-gray-600 bg-gray-50 border-gray-200';
    }
  };

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" onClick={onClose} />

        <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-4xl sm:w-full">
          <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center">
                <SparklesIcon className="h-6 w-6 text-primary-600 mr-2" />
                <h3 className="text-lg font-medium text-gray-900">Generate AI Reply</h3>
              </div>
              <button
                onClick={onClose}
                className="text-gray-400 hover:text-gray-600"
              >
                <XMarkIcon className="h-6 w-6" />
              </button>
            </div>

            <div className="space-y-4">
              {/* Tone Selection */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Tone
                </label>
                <select
                  value={request.tone}
                  onChange={(e) => setRequest({ ...request, tone: e.target.value })}
                  className="block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm"
                >
                  <option value="professional">Professional</option>
                  <option value="friendly">Friendly</option>
                  <option value="formal">Formal</option>
                  <option value="casual">Casual</option>
                </select>
              </div>

              {/* Style Selection */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Style
                </label>
                <select
                  value={request.style}
                  onChange={(e) => setRequest({ ...request, style: e.target.value })}
                  className="block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm"
                >
                  <option value="concise">Concise</option>
                  <option value="detailed">Detailed</option>
                  <option value="empathetic">Empathetic</option>
                </select>
              </div>

              {/* Additional Context */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Additional Context (Optional)
                </label>
                <textarea
                  value={request.additionalContext || ''}
                  onChange={(e) => setRequest({ ...request, additionalContext: e.target.value })}
                  rows={3}
                  className="block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm"
                  placeholder="Add any additional context or specific instructions..."
                />
              </div>

              {/* Feedback Options */}
              <div className="space-y-2">
                <label className="block text-sm font-medium text-gray-700">
                  Include Feedback
                </label>
                <div className="space-y-2">
                  <label className="flex items-center">
                    <input
                      type="checkbox"
                      checked={request.includeToneFeedback}
                      onChange={(e) => setRequest({ ...request, includeToneFeedback: e.target.checked })}
                      className="rounded border-gray-300 text-primary-600 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                    />
                    <span className="ml-2 text-sm text-gray-700">Tone feedback</span>
                  </label>
                  <label className="flex items-center">
                    <input
                      type="checkbox"
                      checked={request.includeClarityFeedback}
                      onChange={(e) => setRequest({ ...request, includeClarityFeedback: e.target.checked })}
                      className="rounded border-gray-300 text-primary-600 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                    />
                    <span className="ml-2 text-sm text-gray-700">Clarity feedback</span>
                  </label>
                </div>
              </div>
            </div>
          </div>

          {/* Generated Reply Section */}
          {generateReplyMutation.data && (
            <div className="bg-gray-50 px-4 py-4 sm:px-6">
              <h4 className="text-sm font-medium text-gray-900 mb-3">Generated Reply</h4>
              <div className="bg-white p-4 rounded-lg border border-gray-200 mb-4">
                <pre className="whitespace-pre-wrap text-sm text-gray-900">
                  {generateReplyMutation.data.generatedReply}
                </pre>
              </div>

              {/* Feedback Section */}
              {(generateReplyMutation.data.toneFeedback.length > 0 || generateReplyMutation.data.clarityFeedback.length > 0) && (
                <div className="space-y-4">
                  {generateReplyMutation.data.toneFeedback.length > 0 && (
                    <div>
                      <h5 className="text-sm font-medium text-gray-900 mb-2">Tone Feedback</h5>
                      <div className="space-y-2">
                        {generateReplyMutation.data.toneFeedback.map((feedback: FeedbackItem, index: number) => (
                          <div
                            key={index}
                            className={`p-3 rounded-lg border ${getSeverityColor(feedback.severity)}`}
                          >
                            <div className="flex items-start">
                              <div className="flex-1">
                                <p className="text-sm font-medium">{feedback.category}</p>
                                <p className="text-sm mt-1">{feedback.suggestion}</p>
                                <p className="text-xs mt-1 opacity-75">{feedback.reason}</p>
                              </div>
                              <span className={`text-xs px-2 py-1 rounded-full ${getSeverityColor(feedback.severity)}`}>
                                {feedback.severity}
                              </span>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {generateReplyMutation.data.clarityFeedback.length > 0 && (
                    <div>
                      <h5 className="text-sm font-medium text-gray-900 mb-2">Clarity Feedback</h5>
                      <div className="space-y-2">
                        {generateReplyMutation.data.clarityFeedback.map((feedback: FeedbackItem, index: number) => (
                          <div
                            key={index}
                            className={`p-3 rounded-lg border ${getSeverityColor(feedback.severity)}`}
                          >
                            <div className="flex items-start">
                              <div className="flex-1">
                                <p className="text-sm font-medium">{feedback.category}</p>
                                <p className="text-sm mt-1">{feedback.suggestion}</p>
                                <p className="text-xs mt-1 opacity-75">{feedback.reason}</p>
                              </div>
                              <span className={`text-xs px-2 py-1 rounded-full ${getSeverityColor(feedback.severity)}`}>
                                {feedback.severity}
                              </span>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>
          )}

          {/* Action Buttons */}
          <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
            <button
              type="button"
              onClick={handleGenerate}
              disabled={generateReplyMutation.isPending}
              className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-primary-600 text-base font-medium text-white hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 sm:ml-3 sm:w-auto sm:text-sm disabled:opacity-50"
            >
              {generateReplyMutation.isPending ? (
                <>
                  <div className="spinner mr-2"></div>
                  Generating...
                </>
              ) : (
                'Generate Reply'
              )}
            </button>
            <button
              type="button"
              onClick={onClose}
              className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm"
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AiReplyGenerator;
